// Define a function for building the table
function buildTable(data) {
  // Clear out any existing data in the table (so that we don't have duplicate entries in the table)
  tbody.html("");

  for (i = 0; i < 100; i++){
    // Append a row to the table body
    let row = tbody.append("tr");

    for (j = 0; j < tableColumns.length; j++){
        column = tableColumns[j];
        let cell = row.append("td");
        cell.text(data[column][i]);
    }

  }
}

// Use this function to update the filters
function updateFilters() {
  // Save the element that was changed as a variable
  let changedelement = d3.select(this);

  //Save the value that was changed as a variable
  let elementvalue = changedelement.property("value");
  
  // Save the id of the filter that was changed as a variable
  let filterId = changedelement.attr("id");

  // If a filter value was entered then add that filterId and value to the filters list; otherwise, clear that filter from the filters object
  if ((filterId !== "WEATHER-CITY") && (elementvalue)) {
    filters[filterId] = elementvalue;
    console.log(filters);
  }
  else {
    delete filters[filterId];
  }

  // Call function to apply all filters and rebuild the table
  filterTable();  
}
  
// Use this function to filter the table when data is entered
function filterTable() {

  //Set the filtered data to the tableData
  let filteredData = structuredClone(tableData);

  // Loop through all of the filters and keep any data that matches the filter values in the variable created in Step 8
  for (const [key, value] of Object.entries(filters)) {
    for (i = 0; i < 100; i++){
        if (filteredData[key][i]){        
            if (filteredData[key][i] != value) {
                for (j = 0; j < tableColumns.length; j++){
                    column = tableColumns[j];
                    delete filteredData[column][i]
                }
            }
        }
    }
  }

  // Finally, rebuild the table using the filtered data
  buildTable(filteredData);
}

